import {Modal,ModalBody} from 'reactstrap';
import React, { Component } from 'react'
import './GetStarted.css';
import success_checked from './assets/checked.png';

class GetStarted extends Component {

  state = { name: '',
            email:'',
            phoneNo:''
          };

  handleSubmit = (event) => {
    event.preventDefault();
    const data = {
    email: this.state.email,
    name: this.state.name,
    phone: this.state.phoneNo,
    Platform: "ORO-AMC",
    type: "ORO-AMC-CONTACT_FORM"
  }

    const requestOptions = {
      method: 'POST',
      headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    }
    fetch('https://www.orowealth.com/funds/userRegistrationInfo', requestOptions)
    .then(response => response.json())
    .then(response => {
        if(response.success) {
          this.setState(
            { 
              modalOpen: true,
              name: '',
              email:'',
              phoneNo:''
            })
        }
    });
  };   
  componentDidMount() {
    this.props.showMenu(false);
  }

  render() {
    return (
      <div className="container-fluid pt-3 bg-img">
          <div className='d-flex d-flex flex-direction-row justify-content-center pt-5'>
              <div className="col-sm-12 col-md-8 col-lg-5">
                <div className="card w-100">
                  <div className="card-body w-100">
                    <form className="login-form pt-4" onSubmit={this.handleSubmit}>
                      <h1 className="form-title">Enter your details to register</h1>

                      <div className="form-group mt-5">
                        <input className="w-100 input-fields" 
                        name="name" 
                        autoComplete="on" 
                        id="name" 
                        placeholder="Name" 
                        type="text" value={this.state.name} 
                        onChange={event => this.setState({ name: event.target.value })}
                        required/>
                        <p className="line" />
                      </div>

                      <div className="form-group mt-5">
                        <input className="w-100 input-fields" 
                        name="email" 
                        autoComplete="on" 
                        id="email" 
                        placeholder="Email Id" 
                        type="email" value={this.state.email} 
                        onChange={event => this.setState({ email: event.target.value })}
                        required />
                        <p className="line" />
                      </div>  

                      <div className="form-group mt-5 pt-1">
                        <input className="w-100 input-fields" 
                        name="phoneNo" autoComplete="on" 
                        id="phoneNo" placeholder="Contact number" 
                        type="number" value={this.state.phoneNo} 
                        onChange={event => this.setState({ phoneNo: event.target.value })}
                        required />
                        <p className="line" />
                      </div>

                      <div className="cta-block form-group mt-4">
                          <button className="w-100 text-white btn btn-primary mt-5" type="submit">Register</button>
                      </div>

                    </form>

                  </div>
                </div>
              </div>
            </div>    
            <Modal isOpen={this.state.modalOpen}
                toggle={()=>this.setState({modalOpen: !this.state.modalOpen})}
                className="success-bar">
                <ModalBody className="thanks-for-your-inte">
                <img src={success_checked} style={{width:'35px'}}/>&nbsp;Thank you for your interest. We will contact you soon.
                </ModalBody>
              </Modal>
          </div>
      );
    }
   }
    export default GetStarted;
