import React, { useState, useEffect } from 'react';
import './mostTop.css';
import desktopImage from './assets/bg_with_path@3x.png';//desktop
import './assets/bg_with_path@2x.png';//Mobile
import mobileImage from './assets/bg_With_Path.png';
import { BrowserRouter as Route, Link } from "react-router-dom";

const MostTop = () => {
    const imageUrl = useWindowWidth() >= 650 ? desktopImage : mobileImage;

    let buttonStyle = {
        width: "100%",
        margin: "1.5vw auto",
        display: "flex",
        alignItems: "center"
      };

    return (
        <div className="most-top" style={{backgroundImage: `url(${imageUrl})` }}>
            <div className="component-content">
                <div className="container-fluid">
                     <div className='d-flex row flex-direction-row mt-5'>
                        <div className=" mx-auto d-flex justify-content-center align-items-center row p-4 col-md-12">
                            <p className="new-age-portfolio-ma">New Age<br/> Portfolio Management Services<br/> where Data is King</p>
                            <p className="we-use-data-driven-m">We use data driven methods for effective wealth creation across the investment universe in order to avoid human biases.</p>
                        </div>

                        <div style={{ ...buttonStyle, justifyContent: "center" }}>
                        <Link to='/GetStarted'>    
                            <button className="get-started-2"
                                type="default">
                                Get Started
                            </button>
                        </Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

const useWindowWidth = () => {
    const [windowWidth, setWindowWidth ] = useState(window.innerWidth);

    const handleWindowResize = () => {
        setWindowWidth(window.innerWidth);
    };

    useEffect(() => {
        window.addEventListener('resize', handleWindowResize);
        return () => window.removeEventListener('resize', handleWindowResize);
    },[]);

    return windowWidth;
};

export default MostTop;
