import React from 'react';
import styled from 'styled-components';
import '../../node_modules/font-awesome/css/font-awesome.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faFacebookF, faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Footer.css';
function Footer() {
    return (
        <FooterContainer className="main-footer pt-5" style={{backgroundColor:'#0F1E36', color:'#ffffff', marginTop:'12vw'}}>
           <div className="footer-left">
                <div className="container">
                    <div className="row">

                        <div className="col-md-6 col-sm-12">
                            <p className="heading">ABOUT US</p>
                            <p className="orowealth-pms-is-a-p">Orowealth PMS is a product of <a href="https://www.orowealth.com/">Orowealth</a> â€” an investment platform for informed investors to make better decisions. Invest with us, and experience the difference that solid, unbiased advice can make.</p>
                        </div>

                        <div className="col-md-3 col-sm-6 links-info">
                        <p className="heading">LINKS & INFO</p>
                            <ul className="list-unstyled">
                                <li><a href="https://www.orowealth.com/terms-of-use/">Terms & Conditions</a></li>
                                <li><a href="https://www.orowealth.com/privacy-policy/">Privacy Policy</a></li>
                                <li><a href="https://www.orowealth.com/disclaimer/">Disclaimer</a></li>
                                <li><a href="https://www.orowealth.com/why-oro/">FAQs</a></li>
                            </ul>
                        </div>

                        <div className="col-md-3 col-sm-6">
                        <p className="heading">GET IN TOUCH </p>
                            <ul className="list-unstyled">
                                <li><a href="https://www.orowealth.com/contact/">Contact Us</a></li>
                                <li><a href="https://mail.google.com/mail/u/0/#inbox?compose=new" target="_blank" rel="noopener noreferrer">connect@oroamc.com</a></li>
                                <li><a href="https://www.facebook.com/orowealth" target="blank" rel="noopener noreferrer"><FontAwesomeIcon icon={faFacebookF} /></a>
                                    <a href="https://twitter.com/orowealth" target="blank" rel="noopener noreferrer"><FontAwesomeIcon className="ml-3" icon={faTwitter} /></a>
                                    <a href="https://www.linkedin.com/company/oro-wealth/" target="blank" rel="noopener noreferrer"><FontAwesomeIcon className="ml-3" icon={faLinkedin} /></a>
                                </li>

                            </ul>

                        </div>
                    </div>
                    <div className="footer-bottom">
                        <p className="direct-mutual-funds">Direct mutual funds charge you no hidden commission for managing your money. This is the reason why direct MF schemes have a lower expense ratio. A lower expense ratio of direct plans allows you to generate higher returns as compared to the regular mutual fund plans. In the long run, the direct mutual fund schemes can give you significantly higher returns than the regular ones.<br/><br/>
                            We respect privacy. We would not access or read your email for anything except the transaction statement. Control is in your hands. You can turn this feature off anytime from the settings in your inbox. Fully Secured. Trusted by 2,00,000+ Investors.<br/><br/>

                            For compliance pertaining to mutual funds please write to admin@orowealth.com. Please ensure you carefully read the Risk Disclosure Document as prescribed by SEBI. Procedure to file a complaint on SEBI SCORES: Register on the SCORES portal (https://scores.gov.in/scores/welcome.html). Mandatory details for filing complaints on SCORES: Name, PAN, Address, Mobile Number, E-mail ID. Benefits: Effective Communication, Speedy redressal of the grievances.</p>
                        <p className="orowealth-alpha-fi pt-5">&copy; Orowealth Alpha Fintech Pvt. Ltd. SEBI registered PMS (INP000006882) and investment advisor (INA000010089 & INA000005549)</p>
                    </div>
                </div>
            </div>
        </FooterContainer>
    );
}

export default Footer;

const FooterContainer = styled.footer`


.footer-left{
    background: var(--mainDark);
    padding-top: 2rem;
    color: var(--mainWhite);
}

.heading{
    text-align: left;
	color: #FFFFFF;
	font-family: "Encode Sans";
	font-size: 14px;
	font-weight: 600;
	line-height: 17px;
}

 .orowealth-pms-is-a-p {
	height: 104px;
	width: 90%;
	opacity: 0.8;
	color: #FFFFFF;
	font-family: "Encode Sans";
	font-size: 14px;
	font-weight: 500;
	line-height: 24px;
    text-align: left;
}
.footer-bottom{
    padding-top: 2rem;
    padding-bottom: 2rem;
}

ul li{
    height:28px;
}

ul li a{
	color: #FFFFFF;
    opacity: 0.8;
	font-family: "Encode Sans";
	font-size: 14px;
	font-weight: 500;
}

ul li a:hover{
    color: var(--mainLifgtGrey);
}

.direct-mutual-funds {
	opacity: 0.8;
	color: #FFFFFF;
	font-family: "Encode Sans";
	font-size: 14px;
	font-weight: 500;
	line-height: 24px;
    text-align: left;
}

.orowealth-alpha-fi {
	color: #FFFFFF;
	font-family: "Encode Sans";
	font-size: 16px;
	font-weight: 500;
	line-height: 20px;
	text-align: left;
}

`;
