import { expect } from 'chai';
import { removeEmpty } from '../index.js';
describe('Test remove empty', function () {
  it('test array', function () {
    var arr = ['', null, false, 0, undefined, {}, [], '0', 'a'];
    expect(removeEmpty(arr)).to.deep.equal([{}, [], '0', 'a']);
  });
  it('test object', function () {
    var arr = {
      a: '',
      b: null,
      c: 'a'
    };
    expect(removeEmpty(arr)).to.deep.equal({
      c: 'a'
    });
  });
  it('test undefined only', function () {
    var arr = ['', null, false, 0, undefined, {}, [], '0', 'a'];
    expect(removeEmpty(arr, true)).to.deep.equal(['', null, false, 0, {}, [], '0', 'a']);
  });
  it('test exclude key', function () {
    var arr = {
      a: 'a',
      b: 1
    };
    expect(removeEmpty(arr, null, ['b'])).to.deep.equal({
      a: 'a'
    });
  });
  it('test null only', function () {
    var arr = ['a', undefined, null, false];
    expect(removeEmpty(arr, function (v) {
      return null != v;
    })).to.deep.equal(['a', false]);
  });
});