import { expect } from 'chai';
import { defaultQuery as query } from '../queryFrom';
describe('Test query ancestor', function () {
  after(function () {
    return document.body.innerHTML = '';
  });
  it('test closest and polyfill', function () {
    document.body.innerHTML = "\n<main id=\"root\"><div id=\"foo\"><span id=\"bar\"></span></div></main>\n";
    var el = query.one('#bar');
    var foo = query.ancestor(el, '#foo');
    el.closest = undefined;
    var foo1 = query.ancestor(el, '#foo');
    expect(foo.isSameNode(foo1)).to.be["true"];
  });
});