import { win, doc } from '../index.js';
import { expect } from 'chai';
import jsdom from 'jsdom-global';
describe('Test doc in node', function () {
  var cleanup;
  before(function () {
    cleanup = jsdom();
    document = undefined;
    window = undefined;
  });
  after(function () {
    return cleanup();
  });
  it('test doc', function () {
    var d = doc();
    expect(d.__null).to.be["true"];
  });
});
describe('Test win in node', function () {
  var cleanup;
  before(function () {
    cleanup = jsdom();
    document = undefined;
    window = undefined;
  });
  after(function () {
    return cleanup();
  });
  it('test win', function () {
    var w = win();
    expect(w.__null).to.be["true"];
  });
});